import axios from 'axios';
import React from 'react'
import { useState } from 'react'
import { useLocation } from 'react-router-dom';
import classes from '../cssModules/CreatePost.module.css'

const UpdatePost = () => {
    const [updatedTitle, setUpdatedTitle] = useState('');
    const [updatedSubtitle, setUpdatedSubtitle] = useState('');
    const [updatedBody, setUpdatedBody] = useState('');

    const location = useLocation()
    console.log(location.pathname.slice(10, 34))
    const id = location.pathname.slice(10,34)

    const updateSubmitHandler = (e) => {
        e.preventDefault();

        axios.put('http://localhost:5002/')

    }


    return (
        <form onSubmit={updateSubmitHandler}>
            <div className={classes.MidSection}>
                <div className={classes.Container}>
                    <div>
                        <input value={updatedTitle} onChange={(e) => setUpdatedTitle(e.target.value)} type="text" className={classes.Input} placeholder="Title" style={{ marginTop: "20px" }} />
                    </div>
                    <div>
                        <input value={updatedSubtitle} onChange={(e) => setUpdatedSubtitle(e.target.value)} type="text" className={classes.Input} placeholder="Sub Title" />
                    </div>
                    <div>
                        <textarea value={updatedBody} onChange={(e) => setUpdatedBody(e.target.value)} type="textarea" className={classes.Input3} placeholder="Text Here - Minimum Character Count 50..." />
                    </div>
                    <div className={classes.ButtonDiv}>
                        <button type="submit" className={classes.Button}>Post</button>
                    </div>
                </div>
            </div>
        </form>
    )
}

export default UpdatePost
