import React, { useState } from 'react'
import classes from '../cssModules/CreatePost.module.css'
import axios from 'axios'


const CreatePost = (e) => {
  const [title, setTitle] = useState('');
  const [subTitle, setSubtitle] = useState('');
  const [body, setBody] = useState('');



  const onClickSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:5002/api/createPost', {
      title,
      subTitle,
      body
    })
      .then(res => console.log(res))
      .catch(err => console.log(err))
  }



  return (
    <form onSubmit={onClickSubmit}>
      <div>


      <div>
        <label>Title</label>
        <input value={title} onChange={(e) => setTitle(e.target.value)} type="text" />
      </div>
      <div>
        <label>Subtitle</label>
        <input value={subTitle} onChange={(e) => setSubtitle(e.target.value)} type="text" />
      </div>
      <div>
        <label>Blog post body</label>
        <input value={body} onChange={(e) => setBody(e.target.value)} type="textarea" />
      </div>
      <button type="submit">Create Blog Post</button>
      </div>
    </form>
  )
}

export default CreatePost
